import { useEffect, useState } from "react";

function SellerDashboard() {
  const [post, setPost] = useState(null);

  useEffect(() => {
    const savedPost = JSON.parse(localStorage.getItem("sellerPost"));
    if (savedPost) {
      setPost(savedPost);
    }
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Seller Dashboard</h2>

      <button
        onClick={() => (window.location.href = "/seller-post")}
        className="bg-green-600 text-white px-4 py-2 rounded mb-4"
      >
        Post New Design
      </button>

      {post ? (
        <div className="border rounded p-4 shadow bg-white">
          <h3 className="text-lg font-bold">{post.title}</h3>
          <img src={post.imageUrl} alt="design" className="w-full h-64 object-cover mt-2" />
          <p className="mt-2">{post.description}</p>
          <p className="text-sm text-gray-600 mt-1">Contact: {post.contact}</p>
        </div>
      ) : (
        <p>No designs posted yet.</p>
      )}
    </div>
  );
}

export default SellerDashboard;
