import React from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import ChatPanel from "../components/ChatPanel";

export default function ChatPage({ profile }) {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();

  const userRoleId = Number(searchParams.get("userRole"));
  const userId = searchParams.get("userId");
  const otherId = searchParams.get("otherId");

  if (!userId || !otherId || !userRoleId) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen">
        <div className="text-red-700 mb-4">Missing chat parameters.</div>
        <button
          className="px-4 py-2 bg-blue-500 text-white rounded"
          onClick={() => navigate(-1)}
        >Go Back</button>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-green-100 to-blue-100 relative">
      <button
        className="absolute top-6 left-6 px-4 py-2 rounded bg-blue-500 text-white"
        onClick={() => navigate(-1)}
      >
        ← Back
      </button>
      <div className="w-full max-w-2xl">
        <ChatPanel
          userId={userId}
          otherId={otherId}
          userRoleId={userRoleId}
          otherRoleId={userRoleId === 1 ? 2 : 1}
          senderName={profile?.name || "Me"}
        />
      </div>
    </div>
  );
}
