import React, { useState, useRef, useEffect } from "react";
import ChatBox from "../components/ChatBox";

export default function ChatPage({ profile, customerName, customerPhoto }) {
  // If profile is null, show fallback
  if (!profile) {
    return (
      <div className="flex items-center justify-center min-h-screen text-gray-500 text-xl">
        No profile found. Please log in or select a user to chat.
      </div>
    );
  }

  // Simulate customer and seller chat.
  const [messages, setMessages] = useState([
    { sender: "seller", text: "Welcome! How can I help you today?", time: Date.now() }
  ]);
  const [input, setInput] = useState("");
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  function sendMessage() {
    if (!input.trim()) return;
    setMessages((prev) => [
      ...prev,
      { sender: "customer", text: input, time: Date.now() }
    ]);
    setInput("");
    // Simulate seller reply
    setTimeout(() => {
      setMessages((prev) => [
        ...prev,
        { sender: "seller", text: "Thank you for your enquiry! We'll get back to you soon.", time: Date.now() }
      ]);
    }, 1200);
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <div className="bg-white/80 shadow-xl rounded-xl w-full max-w-2xl mt-8 animate__animated animate__fadeIn">
        {/* Chat header */}
        <div className="flex items-center gap-3 p-4 border-b bg-gradient-to-r from-green-200 to-blue-200">
          <img
            src={profile.photoUrl || "/default-profile.png"}
            alt="Profile"
            className="w-12 h-12 rounded-full border-2"
          />
          <div>
            <div className="font-bold text-lg text-green-700">{profile.fullName || "Seller"}</div>
            <div className="text-xs text-blue-700">{profile.tailoringName || ""}</div>
          </div>
        </div>
        {/* Chat messages */}
        <div className="p-6 h-96 overflow-y-auto bg-gradient-to-br from-white to-blue-50">
          {messages.map((msg, idx) => (
            <ChatBox
              key={idx}
              text={msg.text}
              sender={msg.sender}
              customerPhoto={customerPhoto}
              sellerPhoto={profile.photoUrl || "/default-profile.png"}
            />
          ))}
          <div ref={chatEndRef} />
        </div>
        {/* Chat input */}
        <div className="p-4 border-t bg-white flex gap-2">
          <input
            className="flex-1 border rounded px-3 py-2 focus:ring-2 focus:ring-green-300"
            placeholder="Type your message..."
            value={input}
            onChange={e => setInput(e.target.value)}
            onKeyDown={e => e.key === "Enter" && sendMessage()}
          />
          <button className="bg-green-600 text-white px-5 py-2 rounded hover:bg-green-700 transition" onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
}
