import React, { useState, useRef, useEffect } from "react";
import { useParams } from "react-router-dom";

function ChatPanel({ userProfile, seller }) {
  const [messages, setMessages] = useState([
    { sender: "seller", text: "Welcome! How can I help you today?", time: Date.now() }
  ]);
  const [input, setInput] = useState("");
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  function sendMessage() {
    if (!input.trim()) return;
    setMessages(prev => [
      ...prev,
      { sender: "customer", text: input, time: Date.now() }
    ]);
    setInput("");
    // Simulate seller reply
    setTimeout(() => {
      setMessages(prev => [
        ...prev,
        { sender: "seller", text: "Thank you for your enquiry! We'll get back to you soon.", time: Date.now() }
      ]);
    }, 1200);
  }

  return (
    <div className="bg-white/90 shadow-xl rounded-xl w-full mt-8 animate__animated animate__fadeIn">
      {/* Chat header */}
      <div className="flex items-center gap-3 p-4 border-b bg-gradient-to-r from-green-200 to-blue-200">
        <img
          src={seller.photoUrl || seller.photo_url || "/default-profile.png"}
          alt="Seller"
          className="w-12 h-12 rounded-full border-2"
        />
        <div>
          <div className="font-bold text-lg text-green-700">{seller.tailoringName || seller.shop_name || "Seller"}</div>
          <div className="text-xs text-blue-700">{seller.address || seller.shop_address || ""}</div>
        </div>
      </div>
      {/* Chat messages */}
      <div className="p-6 h-96 overflow-y-auto bg-gradient-to-br from-white to-blue-50">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`mb-2 flex ${msg.sender === "customer" ? "justify-end" : "justify-start"}`}
          >
            <div
              className={`p-2 rounded-lg max-w-xs ${
                msg.sender === "customer" ? "bg-green-500 text-white" : "bg-gray-200"
              }`}
            >
              {msg.text}
            </div>
          </div>
        ))}
        <div ref={chatEndRef} />
      </div>
      {/* Chat input */}
      <div className="p-4 border-t bg-white flex gap-2">
        <input
          className="flex-1 border rounded px-3 py-2 focus:ring-2 focus:ring-green-300"
          placeholder="Type your message..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === "Enter" && sendMessage()}
        />
        <button className="bg-green-600 text-white px-5 py-2 rounded hover:bg-green-700 transition" onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default function CustomerDashboard({ sellers, userProfile }) {
  const { sellerId } = useParams();
  const [showChat, setShowChat] = useState(false);

  if (!Array.isArray(sellers) || sellers.length === 0) {
    return <div>No sellers found.</div>;
  }
  const seller = sellers.find(s => String(s.id) === String(sellerId));
  if (!seller) return <div>Seller not found.</div>;

  const designs = Array.isArray(seller.designs)
    ? seller.designs
    : Array.isArray(seller.portfolios)
      ? seller.portfolios.map(d => ({
          imageUrl: d.image_url,
          title: d.title,
          description: d.description,
          contact: d.contact,
        }))
      : [];

  return (
    <div className="flex flex-col items-center p-6">
      <div className="bg-white/80 p-4 rounded-xl shadow-lg mb-8 w-full max-w-2xl">
        <div className="flex items-center gap-6">
          <img
            src={seller.photoUrl || seller.photo_url || "/default-profile.png"}
            alt="Seller"
            className="w-24 h-24 rounded-full border-4 border-green-400 shadow"
          />
          <div>
            <h1 className="text-2xl font-bold text-green-700">{seller.tailoringName || seller.shop_name}</h1>
            {(seller.address || seller.shop_address) && (
              <div className="text-base text-blue-600">{seller.address || seller.shop_address}</div>
            )}
            {(seller.phone || seller.phone_number) && (
              <div className="text-base text-gray-700">{seller.phone || seller.phone_number}</div>
            )}
          </div>
        </div>
        {/* Chat Button */}
        <button
          className="mt-4 bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700"
          onClick={() => setShowChat(s => !s)}
        >
          {showChat ? "Close Chat" : "Chat with Seller"}
        </button>
        {showChat && (
          userProfile && userProfile.id ? (
            <ChatPanel userProfile={userProfile} seller={seller} />
          ) : (
            <div className="text-red-500 text-center p-4">
              User profile not loaded. Please log in to chat with seller.
            </div>
          )
        )}
      </div>
      <h2 className="text-xl font-semibold text-blue-700 mb-4">All Designs</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 w-full max-w-5xl">
        {designs.length > 0 ? (
          designs.map((design, idx) => (
            <div
              key={idx}
              className="bg-white rounded-lg shadow p-4 flex flex-col items-center"
            >
              <img src={design.imageUrl || design.image_url} alt={design.title} className="h-32 w-full object-cover rounded mb-2" />
              <div className="text-lg font-semibold text-green-700">{design.title}</div>
              <div className="text-gray-600">{design.description}</div>
              {design.contact && (
                <div className="text-gray-500 text-xs mt-1">{design.contact}</div>
              )}
            </div>
          ))
        ) : (
          <div className="col-span-full text-center text-gray-500 my-8 w-full">
            No designs posted yet.
          </div>
        )}
      </div>
    </div>
  );
}
