import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import Home from "./pages/Home";
import SellerDashboard from "./pages/SellerDashboard";
import CustomerDashboard from "./pages/CustomerDashboard";
import ChatPage from "./pages/ChatPage";
import Login from "./pages/Login";
import Register from "./pages/Register";
import { demoDesigns } from "./data/demoDesigns";

function App() {
  // Central profile state (shared)
  const [profile, setProfile] = useState({
    fullName: "John Doe",
    address: "123 Tailoring Street, City",
    phone: "+91 9876543210",
    tailoringName: "John's Tailors",
    email: "john@example.com",
    photoUrl: "https://randomuser.me/api/portraits/men/32.jpg"
  });
  // Central design state (shared)
  const [designs, setDesigns] = useState(demoDesigns);

  return (
    <Router>
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-green-50 to-blue-100 animate-bgMove">
        <Routes>
          <Route path="/" element={<Home profile={profile} designs={designs} />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/seller" element={
            <SellerDashboard
              profile={profile}
              setProfile={setProfile}
              designs={designs}
              setDesigns={setDesigns}
            />
          } />
          <Route path="/customer" element={
            <CustomerDashboard
              profile={profile}
              designs={designs}
            />
          } />
          <Route path="/chat" element={
            <ChatPage
              profile={profile}
              customerName="Sneha Customer"
              customerPhoto="https://randomuser.me/api/portraits/women/65.jpg"
            />
          } />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
      {/* Animated background style */}
      <style>
        {`
        .animate-bgMove {
          background-size: 400% 400%;
          animation: gradientBG 15s ease infinite;
        }
        @keyframes gradientBG {
          0% {background-position: 0% 50%;}
          50% {background-position: 100% 50%;}
          100% {background-position: 0% 50%;}
        }
        `}
      </style>
    </Router>
  );
}

export default App;
