import { useState, useRef } from "react";

function SellerDashboard() {
  const [designs, setDesigns] = useState([]);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    imageUrl: "",
    contact: ""
  });

  // Seller profile data state
  const [profile, setProfile] = useState({
    fullName: "John Doe",
    address: "123 Tailoring Street, City",
    phone: "+91 9876543210",
    tailoringName: "John's Tailors",
    email: "john@example.com",
    photoUrl: "https://via.placeholder.com/80" // circle photo
  });

  const [showModal, setShowModal] = useState(false);
  const [editProfile, setEditProfile] = useState(false);

  const titleInputRef = useRef(null);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleProfileChange = (e) => {
    setProfile((prev) => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setDesigns((prev) => [...prev, formData]);
    setFormData({ title: "", description: "", imageUrl: "", contact: "" });

    // Scroll top and focus title input
    window.scrollTo({ top: 0, behavior: "smooth" });
    setTimeout(() => {
      titleInputRef.current?.focus();
    }, 300);
  };

  const toggleModal = () => {
    setShowModal((prev) => !prev);
    setEditProfile(false); // reset edit mode on close/open
  };

  const saveProfile = () => {
    // For now just exit edit mode
    setEditProfile(false);
  };

  return (
    <div className="flex max-w-6xl mx-auto p-6 gap-6 relative">
      {/* Left side: Form + Designs */}
      <div className="flex-1">
        <h2 className="text-2xl font-bold mb-4">Post a New Design</h2>
        <form
          onSubmit={handleSubmit}
          className="space-y-4 bg-white p-4 shadow-md rounded mb-6"
        >
          <input
            ref={titleInputRef}
            className="w-full border p-2"
            type="text"
            name="title"
            placeholder="Design Title"
            value={formData.title}
            onChange={handleChange}
            required
          />
          <textarea
            className="w-full border p-2"
            name="description"
            placeholder="Description"
            value={formData.description}
            onChange={handleChange}
            required
          />
          <input
            className="w-full border p-2"
            type="text"
            name="imageUrl"
            placeholder="Image URL"
            value={formData.imageUrl}
            onChange={handleChange}
            required
          />
          <input
            className="w-full border p-2"
            type="text"
            name="contact"
            placeholder="Contact Info"
            value={formData.contact}
            onChange={handleChange}
            required
          />
          <button
            type="submit"
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Post Design
          </button>
        </form>

        <h3 className="text-xl font-semibold mb-2">Your Designs</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {designs.map((design, index) => (
            <div
              key={index}
              className="border p-4 rounded shadow bg-white"
            >
              <img
                src={design.imageUrl}
                alt="Design"
                className="w-full h-48 object-cover rounded"
              />
              <h4 className="text-lg font-bold mt-2">{design.title}</h4>
              <p>{design.description}</p>
              <p className="text-sm text-gray-500 mt-1">
                Contact: {design.contact}
              </p>
            </div>
          ))}
        </div>
      </div>

      {/* Right side: Seller Profile Circle */}
      <div className="w-32 flex flex-col items-center">
        <img
          src={profile.photoUrl}
          alt="Seller"
          className="rounded-full w-24 h-24 object-cover cursor-pointer border-2 border-green-600"
          onClick={toggleModal}
          title="View Profile"
        />
        <p className="mt-2 font-semibold">{profile.fullName}</p>
      </div>

      {/* Modal */}
      {showModal && (
        <div
          className="fixed top-0 right-0 h-full w-full md:w-96 bg-white shadow-lg p-6
          transform transition-transform duration-500 ease-in-out
          z-50
          "
          style={{ animation: "slideIn 0.5s forwards" }}
        >
          <button
            onClick={toggleModal}
            className="mb-4 text-red-600 font-bold"
          >
            Close ✕
          </button>

          {!editProfile ? (
            <>
              <img
                src={profile.photoUrl}
                alt="Seller"
                className="rounded-full w-28 h-28 object-cover mb-4 mx-auto"
              />
              <p><strong>Full Name:</strong> {profile.fullName}</p>
              <p><strong>Address:</strong> {profile.address}</p>
              <p><strong>Phone:</strong> {profile.phone}</p>
              <p><strong>Tailoring Name:</strong> {profile.tailoringName}</p>
              <p><strong>Email:</strong> {profile.email}</p>

              <button
                onClick={() => setEditProfile(true)}
                className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
              >
                Edit Profile
              </button>
            </>
          ) : (
            <>
              <div className="space-y-3">
                <label>
                  Full Name
                  <input
                    type="text"
                    name="fullName"
                    value={profile.fullName}
                    onChange={handleProfileChange}
                    className="w-full border p-2 rounded"
                  />
                </label>
                <label>
                  Address
                  <input
                    type="text"
                    name="address"
                    value={profile.address}
                    onChange={handleProfileChange}
                    className="w-full border p-2 rounded"
                  />
                </label>
                <label>
                  Phone
                  <input
                    type="text"
                    name="phone"
                    value={profile.phone}
                    onChange={handleProfileChange}
                    className="w-full border p-2 rounded"
                  />
                </label>
                <label>
                  Tailoring Name
                  <input
                    type="text"
                    name="tailoringName"
                    value={profile.tailoringName}
                    onChange={handleProfileChange}
                    className="w-full border p-2 rounded"
                  />
                </label>
                <label>
                  Email
                  <input
                    type="email"
                    name="email"
                    value={profile.email}
                    onChange={handleProfileChange}
                    className="w-full border p-2 rounded"
                  />
                </label>
              </div>
              <button
                onClick={saveProfile}
                className="mt-4 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
              >
                Save
              </button>
            </>
          )}
        </div>
      )}

      {/* Add slide-in keyframe animation */}
      <style>
        {`
          @keyframes slideIn {
            from {
              transform: translateX(100%);
              opacity: 0;
            }
            to {
              transform: translateX(0);
              opacity: 1;
            }
          }
        `}
      </style>
    </div>
  );
}

export default SellerDashboard;
