import React from "react";
import { useNavigate } from "react-router-dom";

export default function SellerListPage({ sellers }) {
  const navigate = useNavigate();

  return (
    <div className="flex flex-col items-center p-8">
      <h1 className="text-3xl font-bold mb-8 text-green-700">Select a Seller</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 w-full max-w-6xl">
        {sellers.map((seller) => (
          <div
            key={seller.id}
            className="bg-white rounded-xl shadow-lg p-6 flex flex-col items-center hover:scale-105 transition cursor-pointer"
            onClick={() => navigate(`/customer/${seller.id}`)}
          >
            <img src={seller.photoUrl} alt={seller.fullName} className="w-24 h-24 rounded-full border-4 border-green-400 mb-4" />
            <h2 className="text-xl font-bold text-green-700">{seller.fullName}</h2>
            <div className="text-blue-600">{seller.tailoringName}</div>
            {/* Show sample design title & image */}
            {seller.designs[0] && (
              <div className="mt-4 text-center">
                <img src={seller.designs[0].imageUrl} alt={seller.designs[0].title} className="h-32 w-full object-cover rounded mb-2" />
                <div className="text-base text-gray-800 font-semibold">{seller.designs[0].title}</div>
              </div>
            )}
            <div className="mt-3 text-gray-500 text-sm">{seller.phone}</div>
            <div className="text-gray-400 text-xs">{seller.address}</div>
          </div>
        ))}
      </div>
    </div>
  );
}
