import { useRef } from "react";

// Maps seller/user info to modal fields for display and editing
function getDisplayProfile(profile) {
  // If seller info exists, prefer those fields
  if (profile.seller) {
    return {
      fullName: profile.seller.shop_name || "",
      tailoringName: profile.seller.gst_number || "",
      address: profile.seller.shop_address || "",
      phone: profile.phone || "",
      email: profile.seller.email || profile.email || "",
      photoUrl: profile.seller.photo_url || "/default.png",
    };
  }
  // Fallback: just user
  return {
    fullName: profile.name || "",
    tailoringName: "",
    address: profile.address || "",
    phone: profile.phone || "",
    email: profile.email || "",
    photoUrl: profile.photoUrl || "/default.png",
  };
}

export default function ProfileModal({ profile, setProfile, open, onClose, editable, setEditable }) {
  const fileInput = useRef(null);
  // Use seller data if present, fallback to user
  const displayProfile = getDisplayProfile(profile);

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (ev) => {
      // Save to seller.photo_url if seller exists, else photoUrl
      setProfile(prev =>
        prev.seller
          ? { ...prev, seller: { ...prev.seller, photo_url: ev.target.result } }
          : { ...prev, photoUrl: ev.target.result }
      );
    };
    reader.readAsDataURL(file);
  };

  const handleFieldChange = (field) => (e) => {
    // Save to seller if field is part of seller profile, else user
    setProfile(prev => {
      if (prev.seller) {
        let sellerFields = {
          fullName: "shop_name",
          tailoringName: "gst_number",
          address: "shop_address",
          email: "email"
        };
        if (field in sellerFields) {
          return {
            ...prev,
            seller: { ...prev.seller, [sellerFields[field]]: e.target.value }
          };
        }
      }
      // Else update user fields
      return { ...prev, [field]: e.target.value };
    });
  };

  return (
    <div className={`fixed inset-0 z-50 flex items-center justify-center bg-black/30 transition-opacity duration-300 ${open ? "opacity-100 pointer-events-auto" : "opacity-0 pointer-events-none"}`}>
      <div className={`bg-white rounded-lg shadow-xl max-w-md w-full p-6 transform transition-all duration-300 ${open ? "scale-100" : "scale-95"}`}>
        <button className="absolute top-4 right-7 text-2xl text-gray-400 hover:text-red-500" onClick={onClose}>&times;</button>
        <div className="flex flex-col items-center">
          <div
            className="w-28 h-28 rounded-full overflow-hidden border-4 border-green-600 shadow cursor-pointer mb-2 relative group"
            onClick={() => editable && fileInput.current?.click()}
            title={editable ? "Change photo" : ""}
          >
            <img src={displayProfile.photoUrl} alt="profile" className="w-full h-full object-cover" />
            {editable && <div className="absolute inset-0 flex items-center justify-center bg-black/30 opacity-0 group-hover:opacity-100 transition-opacity"><span className="bg-white text-xs px-2 py-1 rounded">Change</span></div>}
          </div>
          <input ref={fileInput} type="file" accept="image/*" className="hidden" onChange={handlePhotoChange} />
          {!editable ? (
            <>
              <h3 className="font-bold text-xl">{displayProfile.fullName}</h3>
              <div className="text-gray-500 mb-2">{displayProfile.tailoringName}</div>
              <div className="mb-2 text-left w-full">
                <div><b>Address:</b> {displayProfile.address}</div>
                <div><b>Phone:</b> {displayProfile.phone}</div>
                <div><b>Email:</b> {displayProfile.email}</div>
              </div>
              <button className="bg-blue-600 text-white px-4 py-2 rounded mt-2 hover:bg-blue-700 transition" onClick={() => setEditable(true)}>Edit Profile</button>
            </>
          ) : (
            <form className="space-y-2 w-full" onSubmit={e => { e.preventDefault(); setEditable(false); }}>
              <input className="w-full p-2 border rounded" value={displayProfile.fullName} onChange={handleFieldChange("fullName")} placeholder="Shop Name" />
              <input className="w-full p-2 border rounded" value={displayProfile.tailoringName} onChange={handleFieldChange("tailoringName")} placeholder="GST Number" />
              <input className="w-full p-2 border rounded" value={displayProfile.address} onChange={handleFieldChange("address")} placeholder="Address" />
              <input className="w-full p-2 border rounded" value={displayProfile.phone} onChange={handleFieldChange("phone")} placeholder="Phone" />
              <input className="w-full p-2 border rounded" value={displayProfile.email} onChange={handleFieldChange("email")} placeholder="Email" />
              <div className="flex gap-2">
                <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition">Save</button>
                <button type="button" className="bg-gray-400 text-white px-4 py-2 rounded hover:bg-gray-500 transition" onClick={() => setEditable(false)}>Cancel</button>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
}
