import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import DesignCard from "../components/DesignCard";

export default function CustomerDashboard({ sellers }) {
  const { sellerId } = useParams();
  const navigate = useNavigate();

  if (!Array.isArray(sellers) || sellers.length === 0) {
    return <div>No sellers found.</div>;
  }

  const seller = sellers.find(s => String(s.id) === String(sellerId));
  if (!seller) return <div>Seller not found.</div>;

  return (
    <div className="flex flex-col items-center p-6">
      {/* Seller Profile Card */}
      <div className="bg-white/80 p-4 rounded-xl shadow-lg mb-8 w-full max-w-2xl">
        <div className="flex items-center gap-6">
          <img
            src={seller.photoUrl || "/default-profile.png"}
            alt="Seller"
            className="w-24 h-24 rounded-full border-4 border-green-400 shadow"
          />
          <div>
            <h1 className="text-2xl font-bold text-green-700">{seller.tailoringName}</h1>
            {seller.address && <div className="text-base text-blue-600">{seller.address}</div>}
            {seller.phone && <div className="text-base text-gray-700">{seller.phone}</div>}
            {seller.email && <div className="text-sm text-gray-500">{seller.email}</div>}
          </div>
        </div>
        <button
          className="mt-4 bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700"
          onClick={() => navigate("/chat", { state: { seller } })}
        >
          Chat with Seller
        </button>
      </div>

      {/* All Designs */}
      <h2 className="text-xl font-semibold text-blue-700 mb-4">All Designs</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 w-full max-w-5xl">
        {Array.isArray(seller.designs) && seller.designs.length > 0 ? (
          seller.designs.map((design, idx) => (
            <DesignCard
              key={idx}
              design={{
                imageUrl: design.imageUrl,
                title: design.title,
                description: design.description,
                contact: seller.phone, // or design.contact if exists
              }}
              onChat={() => navigate("/chat", { state: { seller } })}
              animate
            />
          ))
        ) : (
          <div className="col-span-full text-center text-gray-500 my-8 w-full">
            No designs posted yet.
          </div>
        )}
      </div>
    </div>
  );
}
