import React, { useState, useRef } from "react";
import ProfileModal from "../components/ProfileModal";

function SellerDashboard({ profile, setProfile, designs, setDesigns }) {
  const [showModal, setShowModal] = useState(false);
  const [editProfile, setEditProfile] = useState(false);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    imageUrl: "",
    contact: ""
  });
  const [editingIndex, setEditingIndex] = useState(null);
  const titleInputRef = useRef(null);

  // Handle input changes for design form
  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  // Handle submit design (add or edit)
  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingIndex === null) {
      setDesigns((prev) => [...prev, formData]);
    } else {
      setDesigns((prev) => {
        const newDesigns = [...prev];
        newDesigns[editingIndex] = formData;
        return newDesigns;
      });
    }
    setFormData({ title: "", description: "", imageUrl: "", contact: "" });
    setEditingIndex(null);
    window.scrollTo({ top: 0, behavior: "smooth" });
    setTimeout(() => { titleInputRef.current?.focus(); }, 300);
  };

  // Handle edit button for each design
  const handleEditDesign = (index) => {
    setEditingIndex(index);
    setFormData(designs[index]);
    window.scrollTo({ top: 0, behavior: "smooth" });
    setTimeout(() => { titleInputRef.current?.focus(); }, 300);
  };

  return (
    <div className="flex flex-col items-center p-6">
      {/* Profile top bar */}
      <div className="flex justify-end w-full">
        <img
          src={profile.photoUrl}
          alt="Seller"
          className="rounded-full w-20 h-20 object-cover border-2 border-green-600 cursor-pointer"
          onClick={() => setShowModal(true)}
          title="View Profile"
        />
      </div>

      <ProfileModal
        profile={profile}
        setProfile={setProfile}
        open={showModal}
        onClose={() => { setShowModal(false); setEditProfile(false); }}
        editable={editProfile}
        setEditable={setEditProfile}
      />

      {/* Design form */}
      <div className="max-w-xl w-full bg-white/90 rounded-xl shadow-lg p-6 mt-6">
        <h2 className="text-2xl font-bold mb-4 text-green-700">{editingIndex === null ? "Post a New Design" : "Edit Design"}</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            ref={titleInputRef}
            className="w-full border p-2 rounded"
            type="text"
            name="title"
            placeholder="Design Title"
            value={formData.title}
            onChange={handleChange}
            required
          />
          <textarea
            className="w-full border p-2 rounded"
            name="description"
            placeholder="Description"
            value={formData.description}
            onChange={handleChange}
            required
          />
          <input
            className="w-full border p-2 rounded"
            type="text"
            name="imageUrl"
            placeholder="Image URL"
            value={formData.imageUrl}
            onChange={handleChange}
            required
          />
          <input
            className="w-full border p-2 rounded"
            type="text"
            name="contact"
            placeholder="Contact Info"
            value={formData.contact}
            onChange={handleChange}
            required
          />
          <div>
            <button
              type="submit"
              className={`px-4 py-2 rounded transition ${
                editingIndex === null
                  ? "bg-green-600 hover:bg-green-700 text-white"
                  : "bg-yellow-600 hover:bg-yellow-700 text-white"
              }`}
            >
              {editingIndex === null ? "Post Design" : "Save Changes"}
            </button>
            {editingIndex !== null && (
              <button
                type="button"
                onClick={() => {
                  setEditingIndex(null);
                  setFormData({ title: "", description: "", imageUrl: "", contact: "" });
                }}
                className="ml-4 px-4 py-2 rounded bg-gray-400 hover:bg-gray-500 text-white"
              >
                Cancel
              </button>
            )}
          </div>
        </form>
      </div>

      {/* Designs List */}
      <h3 className="text-xl font-semibold my-6 text-blue-700">Your Designs</h3>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 w-full max-w-5xl">
        {designs.map((design, idx) => (
          <div
            key={idx}
            className="border p-4 rounded shadow bg-white cursor-pointer hover:scale-105 transition"
            onClick={() => handleEditDesign(idx)}
            title="Click to edit"
          >
            <img src={design.imageUrl} alt="Design" className="w-full h-48 object-cover rounded" />
            <h4 className="text-lg font-bold mt-2">{design.title}</h4>
            <p>{design.description}</p>
            <p className="text-sm text-gray-500 mt-1">Contact: {design.contact}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default SellerDashboard;
