import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import SellerDashboard from "./pages/SellerDashboard";
import CustomerDashboard from "./pages/CustomerDashboard";
import ChatPage from "./pages/ChatPage";
import ChatMutal from "./pages/ChatMutal";

function App() {
  // Centralized seller profile state
  const [profile, setProfile] = useState({
    fullName: "John Doe",
    address: "123 Tailoring Street, City",
    phone: "+91 9876543210",
    tailoringName: "John's Tailors",
    email: "john@example.com",
    photoUrl: "https://via.placeholder.com/80"
  });

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        {/* Pass profile and setProfile as props to all components that use or edit seller profile */}
        <Route path="/seller" element={<SellerDashboard profile={profile} setProfile={setProfile} />} />
        <Route path="/customer" element={<CustomerDashboard />} />
        {/* If ChatMutal uses seller profile, pass it too */}
        <Route path="/chat" element={<ChatMutal profile={profile} setProfile={setProfile} />} />
        {/* <Route path="/chat-simple" element={<ChatPage />} /> */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
