import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import ChatPanel from "../components/ChatPanel";

export default function ChatPage({ profile }) {
  const { userRole, userId, otherId } = useParams();
  const navigate = useNavigate();

  if (!userId || !otherId || !userRole) {
    return (
      <div>Missing chat parameters</div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-green-100 to-blue-100 relative">
      <button
        className="absolute top-6 left-6 px-4 py-2 rounded bg-blue-500 text-white"
        onClick={() => navigate(-1)}
      >
        ← Back
      </button>
      <div className="w-full max-w-2xl">
        <ChatPanel
          userId={userId}
          otherId={otherId}
          userRoleId={Number(userRole)}
          otherRoleId={Number(userRole) === 1 ? 2 : 1}
          senderName={profile?.name || "Me"}
        />
      </div>
    </div>
  );
}
