import React, { useState, useEffect } from "react";
import ProfileModal from "../components/ProfileModal";
import SellerChatPanel from "../components/SellerChatPanel";

function getDisplayProfile(profile) {
  if (!profile) return {
    fullName: "",
    tailoringName: "",
    address: "",
    phone: "",
    eid: "",
    email: "",
    photoUrl: "/default.png",
  };
  if (profile.seller) {
    return {
      fullName: profile.seller.shop_name || "",
      tailoringName: profile.seller.gst_number || "",
      address: profile.seller.shop_address || "",
      phone: profile.seller.phone_number || "",
      eid: profile.seller.eid || "",
      email: profile.seller.email || profile.email || "",
      photoUrl: profile.seller.photo_url || "/default.png",
    };
  }
  return {
    fullName: profile.name || "",
    tailoringName: "",
    address: profile.address || "",
    phone: profile.phone || "",
    eid: "",
    email: profile.email || "",
    photoUrl: profile.photoUrl || "/default.png",
  };
}

function SellerDashboard({ profile, setProfile }) {
  const [showModal, setShowModal] = useState(false);
  const [editProfile, setEditProfile] = useState(false);
  const [designs, setDesigns] = useState([]);
  const [chatUsers, setChatUsers] = useState([]);
  const [selectedCustomer, setSelectedCustomer] = useState(null);

  // Fetch seller info & portfolios
  useEffect(() => {
    if (!profile) return;
    let sellerId = profile?.seller?.id || profile?.id;
    if (!profile?.seller && sellerId) {
      fetch(`http://localhost:8000/api/sellers/${sellerId}`)
        .then(res => res.json())
        .then(seller => {
          setProfile(prev => ({ ...prev, seller: seller }));
          setDesigns(seller.portfolios || []);
        });
    } else if (profile?.seller && sellerId) {
      fetch(`http://localhost:8000/api/sellers/${sellerId}`)
        .then(res => res.json())
        .then(seller => setDesigns(seller.portfolios || []));
    }
  }, [profile, setProfile]);

  // Fetch all chat users for seller
  useEffect(() => {
    const sellerId = profile?.seller?.id || profile?.id;
    if (!sellerId) return;
    fetch(`http://localhost:8000/api/chat_s/seller/${sellerId}`)
      .then(res => res.json())
      .then(chats => {
        // Get unique customer IDs and dummy names for demo
        const users = [];
        chats.forEach(chat => {
          // Defensive: skip if chat or sender/receiver is invalid
          if (!chat) return;
          const isSellerSender = chat.sender_id === sellerId;
          const customerId = isSellerSender ? chat.receiver_id : chat.sender_id;
          if (
            customerId !== undefined &&
            customerId !== null &&
            typeof customerId !== "string" &&
            !users.some(u => u.id === customerId) &&
            customerId !== sellerId // prevent self-chat
          ) {
            users.push({ id: customerId, name: `Customer ${customerId}` });
          }
        });
        // Only use users with a valid id
        const validUsers = users.filter(u => u && typeof u.id !== "undefined" && u.id !== null);
        setChatUsers(validUsers);
        // Optionally auto-select first customer
        if (
          !selectedCustomer ||
          !validUsers.some(u => u.id === selectedCustomer?.id)
        ) {
          if (validUsers.length) setSelectedCustomer(validUsers[0]);
          else setSelectedCustomer(null);
        }
      });
    // eslint-disable-next-line
  }, [profile]);

  const displayProfile = getDisplayProfile(profile);

  const validChatUsers = chatUsers.filter(u => u && typeof u.id !== "undefined" && u.id !== null);

  return (
    <div className="flex flex-col items-center p-6">
      <div className="flex justify-end w-full">
        <img
          src={displayProfile.photoUrl}
          alt="Seller"
          className="rounded-full w-20 h-20 object-cover border-2 border-green-600 cursor-pointer"
          onClick={() => setShowModal(true)}
          title="View Profile"
        />
      </div>
      <ProfileModal
        profile={profile}
        setProfile={setProfile}
        open={showModal}
        onClose={() => { setShowModal(false); setEditProfile(false); }}
        editable={editProfile}
        setEditable={setEditProfile}
        onLogout={() => {
          setProfile(null);
          localStorage.removeItem("token");
          window.location.href = "/login";
        }}
      />

      <div className="max-w-xl w-full bg-white/90 rounded-xl shadow-lg p-6 mt-6">
        <h2 className="text-2xl font-bold mb-4 text-green-700">Your Customers</h2>
        <div className="flex flex-row gap-4 mb-6">
          {validChatUsers.length === 0 && <div>No chats yet.</div>}
          {validChatUsers.map(user => (
            <button
              key={user.id}
              className={`px-4 py-2 rounded ${
                selectedCustomer?.id === user.id
                  ? "bg-green-600 text-white"
                  : "bg-gray-200"
              }`}
              onClick={() => setSelectedCustomer(user)}
            >
              {user.name}
            </button>
          ))}
        </div>
        {selectedCustomer && typeof selectedCustomer.id !== "undefined" && selectedCustomer.id !== null ? (
          <SellerChatPanel
            sellerId={profile?.seller?.id || profile?.id}
            customer={selectedCustomer} // Pass the whole object!
            sellerName={displayProfile.fullName}
          />
        ) : (
          <div className="text-gray-400 text-center">Select a customer to chat</div>
        )}
      </div>

      <h3 className="text-xl font-semibold my-6 text-blue-700">Your Designs</h3>
      {designs.length === 0 ? (
        <div className="text-center text-gray-500 my-8 w-full">No designs posted yet.</div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 w-full max-w-5xl">
          {designs.map((design, idx) => (
            <div
              key={design.id || idx}
              className="border p-4 rounded shadow bg-white"
            >
              <img src={design.image_url} alt="Design" className="w-full h-48 object-cover rounded" />
              <h4 className="text-lg font-bold mt-2">{design.title}</h4>
              <p>{design.description}</p>
              <p className="text-sm text-gray-500 mt-1">Contact: {design.contact}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default SellerDashboard;
