import { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import ProfileModal from "../components/ProfileModal"; // <-- Import ProfileModal

function SellerDashboard() {
  // ...all your previous state/logic
  const [showModal, setShowModal] = useState(false);
  const [editProfile, setEditProfile] = useState(false);
  // ...rest of your state

  // ...all your previous functions

  return (
    <div className="flex max-w-6xl mx-auto p-6 gap-6 relative">
      {/* Left side: Form + Designs */}
      <div className="flex-1">
        {/* ...your form and designs code... */}

        {/* Chat button */}
        <div className="flex justify-end mb-4">
          <button
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition shadow"
            onClick={() => navigate("/chat")}
          >
            Customer Chats
          </button>
        </div>
      </div>

      {/* Right side: Seller Profile Circle */}
      <div className="w-32 flex flex-col items-center">
        <img
          src={profile.photoUrl}
          alt="Seller"
          className="rounded-full w-24 h-24 object-cover cursor-pointer border-2 border-green-600"
          onClick={() => setShowModal(true)}
          title="View Profile"
        />
        <p className="mt-2 font-semibold">{profile.fullName}</p>
      </div>

      {/* Profile Modal */}
      <ProfileModal
        profile={profile}
        setProfile={setProfile}
        open={showModal}
        onClose={() => { setShowModal(false); setEditProfile(false); }}
        editable={editProfile}
        setEditable={setEditProfile}
      />
      {/* ...rest of your styles... */}
    </div>
  );
}
export default SellerDashboard;
