import React from "react";

export default function DesignCard({ design, onChat, animate }) {
  return (
    <div className={`bg-white/90 rounded-lg shadow-lg flex flex-col items-center p-4 transition-all
      ${animate ? "hover:scale-105 hover:shadow-2xl animate__animated animate__fadeInUp" : ""}
    `}>
      <img src={design.imageUrl} alt={design.title} className="w-full h-48 object-cover rounded mb-3 shadow" />
      <h3 className="text-lg font-bold text-green-700">{design.title}</h3>
      <p className="text-gray-700">{design.description}</p>
      <div className="text-sm text-gray-500 mt-2 mb-3">Contact: {design.contact}</div>
      <button
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
        onClick={onChat}
      >
        Chat
      </button>
    </div>
  );
}
