import { useState } from "react";
import { useNavigate } from "react-router-dom";

function SellerDashboard() {
  const [designs, setDesigns] = useState([]);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    imageUrl: "",
    contact: ""
  });
const navigate = useNavigate();
  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setDesigns((prev) => [...prev, formData]);
    setFormData({ title: "", description: "", imageUrl: "", contact: "" });
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Post a New Design</h2>
      <form onSubmit={handleSubmit} className="space-y-4 bg-white p-4 shadow-md rounded">
        <input
          className="w-full border p-2"
          type="text"
          name="title"
          placeholder="Design Title"
          value={formData.title}
          onChange={handleChange}
          required
        />
        <textarea
          className="w-full border p-2"
          name="description"
          placeholder="Description"
          value={formData.description}
          onChange={handleChange}
          required
        />
        <input
          className="w-full border p-2"
          type="text"
          name="imageUrl"
          placeholder="Image URL"
          value={formData.imageUrl}
          onChange={handleChange}
          required
        />
        <input
          className="w-full border p-2"
          type="text"
          name="contact"
          placeholder="Contact Info"
          value={formData.contact}
          onChange={handleChange}
          required
        />
        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Post Design
        </button>
      </form>

      <h3 className="text-xl font-semibold mt-6 mb-2">Your Designs</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {designs.map((design, index) => (
          <div key={index} className="border p-4 rounded shadow bg-white">
            <img src={design.imageUrl} alt="Design" className="w-full h-48 object-cover rounded" />
            <h4 className="text-lg font-bold mt-2">{design.title}</h4>
            <p>{design.description}</p>
            <p className="text-sm text-gray-500 mt-1">Contact: {design.contact}</p>
          </div>
        ))}
      </div>
      <button
  onClick={() => navigate("/seller-post")}
  className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
>
  Add New Design
</button>
    </div>
  );
}

export default SellerDashboard;
