import { useState } from "react";

function CustomerDashboard() {
  // Example dummy data (later we’ll fetch this from backend or shared state)
  const [designs] = useState([
    {
      title: "Bridal Lehenga",
      description: "Red bridal lehenga with heavy embroidery.",
      imageUrl: "https://via.placeholder.com/300x200?text=Bridal+Lehenga",
      contact: "seller1@example.com"
    },
    {
      title: "Casual Kurti",
      description: "Light cotton kurti suitable for summer.",
      imageUrl: "https://via.placeholder.com/300x200?text=Casual+Kurti",
      contact: "seller2@example.com"
    }
  ]);

  const startChat = (sellerContact) => {
    alert(`Starting chat with ${sellerContact}`);
  };

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Browse Designs</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {designs.map((design, index) => (
          <div key={index} className="border p-4 rounded shadow bg-white">
            <img src={design.imageUrl} alt={design.title} className="w-full h-48 object-cover rounded" />
            <h3 className="text-lg font-bold mt-2">{design.title}</h3>
            <p>{design.description}</p>
            <p className="text-sm text-gray-600 mt-1">Contact: {design.contact}</p>
            <button
              onClick={() => startChat(design.contact)}
              className="mt-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
              Chat
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CustomerDashboard;
