import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("customer"); // Choose between seller/customer

  function handleLogin(e) {
    e.preventDefault();
    // Simulate login. You can add real auth here.
    if (role === "seller") {
      navigate("/seller");
    } else {
      navigate("/customer");
    }
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-100 to-green-100">
      <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-md">
        <h2 className="text-2xl font-bold mb-6 text-green-700">Login</h2>
        <form className="space-y-4" onSubmit={handleLogin}>
          <input
            type="email"
            className="w-full border rounded p-2"
            placeholder="Email"
            value={email}
            onChange={e => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            className="w-full border rounded p-2"
            placeholder="Password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
          />
          <div className="flex gap-4 items-center">
            <label>
              <input
                type="radio"
                value="customer"
                checked={role === "customer"}
                onChange={() => setRole("customer")}
              />
              Customer
            </label>
            <label>
              <input
                type="radio"
                value="seller"
                checked={role === "seller"}
                onChange={() => setRole("seller")}
              />
              Seller
            </label>
          </div>
          <button className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700 transition">Login</button>
        </form>
        <div className="mt-4 text-center">
          <span>Don't have an account? </span>
          <button className="text-blue-700 underline" onClick={() => navigate("/register")}>Register</button>
        </div>
      </div>
    </div>
  );
}
