import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Register({ setProfile }) {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("customer");
  const [shopName, setShopName] = useState("");
  const [shopAddress, setShopAddress] = useState("");
  const [gstNumber, setGstNumber] = useState("");
  const [photoUrl, setPhotoUrl] = useState("");
  const [error, setError] = useState("");

  async function handleRegister(e) {
    e.preventDefault();
    setError("");
    const payload = { email, password, role };
    if (role === "seller") {
      payload.shop_name = shopName;
      payload.shop_address = shopAddress;
      payload.gst_number = gstNumber;
      payload.photo_url = photoUrl;
    }
    try {
      const res = await fetch("http://localhost:8000/api/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      const data = await res.json();
      if (res.ok) {
        // Set profile state so dashboard(s) work
        setProfile({
          ...data.user,
          seller: data.seller
        });
        if (data.user.role === "seller") {
          navigate("/seller");
        } else {
          navigate("/customer");
        }
      } else {
        if (data.errors) {
          setError(Object.values(data.errors).flat().join(" "));
        } else {
          setError(data.message || "Registration failed");
        }
      }
    } catch (err) {
      setError("Network error");
    }
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-100 to-green-100">
      <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-md">
        <h2 className="text-2xl font-bold mb-6 text-blue-700">Register</h2>
        <form className="space-y-4" onSubmit={handleRegister} autoComplete="on">
          <label htmlFor="register-email" className="sr-only">Email</label>
          <input
            id="register-email"
            name="email"
            type="email"
            className="w-full border rounded p-2"
            placeholder="Email"
            value={email}
            onChange={e => setEmail(e.target.value)}
            required
            autoComplete="email"
          />
          <label htmlFor="register-password" className="sr-only">Password</label>
          <input
            id="register-password"
            name="password"
            type="password"
            className="w-full border rounded p-2"
            placeholder="Password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
            autoComplete="new-password"
          />
          <div className="flex gap-4 items-center">
            <label htmlFor="register-customer">
              <input
                id="register-customer"
                name="role"
                type="radio"
                value="customer"
                checked={role === "customer"}
                onChange={() => setRole("customer")}
              />
              Customer
            </label>
            <label htmlFor="register-seller">
              <input
                id="register-seller"
                name="role"
                type="radio"
                value="seller"
                checked={role === "seller"}
                onChange={() => setRole("seller")}
              />
              Seller
            </label>
          </div>
          {role === "seller" && (
            <>
              <label htmlFor="register-shop-name" className="sr-only">Shop Name</label>
              <input
                id="register-shop-name"
                name="shop_name"
                className="w-full border rounded p-2"
                placeholder="Shop Name"
                value={shopName}
                onChange={e => setShopName(e.target.value)}
                required
                autoComplete="organization"
              />
              <label htmlFor="register-shop-address" className="sr-only">Shop Address</label>
              <input
                id="register-shop-address"
                name="shop_address"
                className="w-full border rounded p-2"
                placeholder="Shop Address"
                value={shopAddress}
                onChange={e => setShopAddress(e.target.value)}
                autoComplete="street-address"
              />
              <label htmlFor="register-gst-number" className="sr-only">GST Number</label>
              <input
                id="register-gst-number"
                name="gst_number"
                className="w-full border rounded p-2"
                placeholder="GST Number"
                value={gstNumber}
                onChange={e => setGstNumber(e.target.value)}
                autoComplete="off"
              />
              <label htmlFor="register-photo-url" className="sr-only">Photo URL</label>
              <input
                id="register-photo-url"
                name="photo_url"
                className="w-full border rounded p-2"
                placeholder="Photo URL"
                value={photoUrl}
                onChange={e => setPhotoUrl(e.target.value)}
                autoComplete="off"
              />
            </>
          )}
          <button className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition">Register</button>
        </form>
        {error && <div className="mt-2 text-red-600">{error}</div>}
        <div className="mt-4 text-center">
          <span>Already have an account? </span>
          <button className="text-green-700 underline" type="button" onClick={() => navigate("/login")}>Login</button>
        </div>
      </div>
    </div>
  );
}yy
