import { Link } from 'react-router-dom';
export default function Home() {
  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-tr from-blue-200 to-green-200">
      <h1 className="text-3xl font-bold mb-6">Welcome to Tailoring Platform</h1>
      <img src="/fashion-illustration.svg" alt="Tailor" className="w-64 mb-8 drop-shadow-lg" />
      <p className="mb-8 text-lg text-gray-700">Please login or register to continue</p>
      <div className="space-x-4">
        <Link to="/login">
          <button className="bg-blue-600 text-white px-5 py-2 rounded shadow hover:bg-blue-700 transition">Login</button>
        </Link>
        <Link to="/register">
          <button className="bg-green-600 text-white px-5 py-2 rounded shadow hover:bg-green-700 transition">Register</button>
        </Link>
      </div>
    </div>
  );
}
