import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import ProfileModal from "../components/ProfileModal";
import ScissorsLoader from "../components/ScissorsLoader";
import SellerChatPanel from "../components/SellerChatPanel";

// ...getDisplayProfile function here as in your code...

function SellerDashboard({ profile, setProfile }) {
  const navigate = useNavigate();
  const [showModal, setShowModal] = useState(false);
  const [editProfile, setEditProfile] = useState(false);
  const [designs, setDesigns] = useState([]);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    imageUrl: "",
    contact: ""
  });
  const [editingIndex, setEditingIndex] = useState(null);
  const [editingId, setEditingId] = useState(null); // Track the id of the design being edited
  const [loading, setLoading] = useState(false);
  const titleInputRef = useRef(null);

  // Fetch seller info and portfolios
  useEffect(() => {
    if (!profile) return;
    let sellerId = profile?.seller?.id || profile?.id;
    setLoading(true);
    if (!profile?.seller && sellerId) {
      fetch(`http://localhost:8000/api/sellers/${sellerId}`)
        .then(res => res.json())
        .then(seller => {
          setProfile(prev => ({
            ...prev,
            seller: seller,
          }));
          setDesigns(seller.portfolios || []);
        })
        .finally(() => setLoading(false));
    } else if (profile?.seller && sellerId) {
      fetch(`http://localhost:8000/api/sellers/${sellerId}`)
        .then(res => res.json())
        .then(seller => setDesigns(seller.portfolios || []))
        .finally(() => setLoading(false));
    }
  }, [profile, setProfile]);

  // Optional: Refresh seller info after profile modal closes
  useEffect(() => {
    if (showModal === false && profile?.seller?.id) {
      setLoading(true);
      fetch(`http://localhost:8000/api/sellers/${profile.seller.id}`)
        .then(res => res.json())
        .then(seller => setProfile(prev => ({
          ...prev,
          seller: seller,
        })))
        .finally(() => setLoading(false));
    }
  }, [showModal, profile?.seller?.id, setProfile]);

  // Logout handler
  const handleLogout = () => {
    setProfile(null);
    localStorage.removeItem("token");
    navigate("/login");
  };

  if (!profile || loading) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen">
        <ScissorsLoader />
        <div className="text-center text-lg mt-4 text-blue-600">Loading...</div>
      </div>
    );
  }

  // ...handleChange, handleSubmit, handleEditDesign as in your code...

  const displayProfile = getDisplayProfile(profile);

  return (
    <div className="flex flex-col items-center p-6">
      {/* Profile top bar */}
      <div className="flex justify-end w-full">
        <img
          src={displayProfile.photoUrl}
          alt="Seller"
          className="rounded-full w-20 h-20 object-cover border-2 border-green-600 cursor-pointer"
          onClick={() => setShowModal(true)}
          title="View Profile"
        />
      </div>

      <ProfileModal
        profile={profile}
        setProfile={setProfile}
        open={showModal}
        onClose={() => { setShowModal(false); setEditProfile(false); }}
        editable={editProfile}
        setEditable={setEditProfile}
        onLogout={handleLogout}
      />

      {/* Design form */}
      <div className="max-w-xl w-full bg-white/90 rounded-xl shadow-lg p-6 mt-6">
        {/* ...Design form as in your code... */}
      </div>

      {/* Seller Chat Panel */}
      {profile?.seller?.id && (
        <SellerChatPanel sellerId={profile.seller.id} sellerProfile={profile.seller} />
      )}

      {/* Designs List */}
      {/* ...Designs List as in your code... */}
    </div>
  );
}

export default SellerDashboard;
