import { useState } from "react";
import { useNavigate } from "react-router-dom";

function ChatPage() {
  const [messages, setMessages] = useState([
    { sender: "seller", text: "Hi! How can I help you?" },
  ]);
  const [input, setInput] = useState("");
  const navigate = useNavigate();

  const sendMessage = () => {
    if (input.trim() === "") return;
    setMessages([...messages, { sender: "customer", text: input }]);
    setTimeout(() => {
      setMessages((prev) => [
        ...prev,
        { sender: "seller", text: "Thanks for your message! I'll reply soon." },
      ]);
    }, 1000);
    setInput("");
  };

  return (
    <div className="max-w-xl mx-auto p-4">
      <h2 className="text-xl font-bold mb-4">Chat with Seller</h2>
      <div className="bg-white border h-80 overflow-y-auto rounded p-3 mb-4 shadow">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`mb-2 ${
              msg.sender === "customer" ? "text-right" : "text-left"
            }`}
          >
            <span
              className={`inline-block px-3 py-2 rounded-lg ${
                msg.sender === "customer"
                  ? "bg-blue-500 text-white"
                  : "bg-gray-200 text-black"
              }`}
            >
              {msg.text}
            </span>
          </div>
        ))}
      </div>
      <div className="flex gap-2">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
          className="flex-1 border rounded px-3 py-2"
        />
        <button
          onClick={sendMessage}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          Send
        </button>
      </div>
      <button
        onClick={() => navigate("/customer")}
        className="mt-4 text-blue-600 underline"
      >
        ← Back to Designs
      </button>
    </div>
  );
}

export default ChatPage;
