import { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";

function SellerDashboard() {
  const [designs, setDesigns] = useState([]);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    imageUrl: "",
    contact: ""
  });

  const [editingIndex, setEditingIndex] = useState(null);

  // Seller profile data state
  const [profile, setProfile] = useState({
    fullName: "John Doe",
    address: "123 Tailoring Street, City",
    phone: "+91 9876543210",
    tailoringName: "John's Tailors",
    email: "john@example.com",
    photoUrl: "https://via.placeholder.com/80" // circle photo
  });

  const [showModal, setShowModal] = useState(false);
  const [editProfile, setEditProfile] = useState(false);

  const titleInputRef = useRef(null);

  // Update form fields for designs
  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  // Update profile fields inside modal
  const handleProfileChange = (e) => {
    setProfile((prev) => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  // Handle profile photo change inside modal
  const handleProfilePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (ev) => {
        setProfile((prev) => ({
          ...prev,
          photoUrl: ev.target.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  // Submit form for adding/editing design
  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingIndex === null) {
      // Add new
      setDesigns((prev) => [...prev, formData]);
    } else {
      // Update existing
      setDesigns((prev) => {
        const newDesigns = [...prev];
        newDesigns[editingIndex] = formData;
        return newDesigns;
      });
    }
    setFormData({ title: "", description: "", imageUrl: "", contact: "" });
    setEditingIndex(null);

    window.scrollTo({ top: 0, behavior: "smooth" });
    setTimeout(() => {
      titleInputRef.current?.focus();
    }, 300);
  };

  // Open edit form for design
  const handleEditDesign = (index) => {
    setEditingIndex(index);
    setFormData(designs[index]);
    window.scrollTo({ top: 0, behavior: "smooth" });
    setTimeout(() => {
      titleInputRef.current?.focus();
    }, 300);
  };

  const toggleModal = () => {
    setShowModal((prev) => !prev);
    setEditProfile(false);
  };

  const saveProfile = () => {
    setEditProfile(false);
  };
  const navigate = useNavigate();

  return (
    <div className="flex max-w-6xl mx-auto p-6 gap-6 relative">
      {/* Left side: Form + Designs */}
      <div className="flex-1">
        <h2 className="text-2xl font-bold mb-4">
          {editingIndex === null ? "Post a New Design" : "Edit Design"}
        </h2>
        <form
          onSubmit={handleSubmit}
          className="space-y-4 bg-white p-4 shadow-md rounded mb-6"
        >
          <input
            ref={titleInputRef}
            className="w-full border p-2"
            type="text"
            name="title"
            placeholder="Design Title"
            value={formData.title}
            onChange={handleChange}
            required
          />
          <textarea
            className="w-full border p-2"
            name="description"
            placeholder="Description"
            value={formData.description}
            onChange={handleChange}
            required
          />
          <input
            className="w-full border p-2"
            type="text"
            name="imageUrl"
            placeholder="Image URL"
            value={formData.imageUrl}
            onChange={handleChange}
            required
          />
          <input
            className="w-full border p-2"
            type="text"
            name="contact"
            placeholder="Contact Info"
            value={formData.contact}
            onChange={handleChange}
            required
          />
          <button
            type="submit"
            className={`px-4 py-2 rounded ${
              editingIndex === null
                ? "bg-green-600 hover:bg-green-700 text-white"
                : "bg-yellow-600 hover:bg-yellow-700 text-white"
            }`}
          >
            {editingIndex === null ? "Post Design" : "Save Changes"}
          </button>
          {editingIndex !== null && (
            <button
              type="button"
              onClick={() => {
                setEditingIndex(null);
                setFormData({ title: "", description: "", imageUrl: "", contact: "" });
              }}
              className="ml-4 px-4 py-2 rounded bg-gray-400 hover:bg-gray-500 text-white"
            >
              Cancel
            </button>
          )}
        </form>

        <h3 className="text-xl font-semibold mb-2">Your Designs</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {designs.map((design, index) => (
            <div
              key={index}
              className="border p-4 rounded shadow bg-white cursor-pointer"
              onClick={() => handleEditDesign(index)}
              title="Click to edit"
            >
              <img
                src={design.imageUrl}
                alt="Design"
                className="w-full h-48 object-cover rounded"
              />
              <h4 className="text-lg font-bold mt-2">{design.title}</h4>
              <p>{design.description}</p>
              <p className="text-sm text-gray-500 mt-1">Contact: {design.contact}</p>
            </div>
          ))}
        </div>
         <div className="flex justify-end mb-4">
      <button
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition shadow"
        onClick={() => navigate("/chat")}
      >
        Customer Chats
      </button>
    </div>
      </div>

      {/* Right side: Seller Profile Circle */}
      <div className="w-32 flex flex-col items-center">
        <img
          src={profile.photoUrl}
          alt="Seller"
          className="rounded-full w-24 h-24 object-cover cursor-pointer border-2 border-green-600"
          onClick={toggleModal}
          title="View Profile"
        />
        <p className="mt-2 font-semibold">{profile.fullName}</p>
      </div>

      {/* Modal */}
      {showModal && (
        <div
          className="fixed top-0 right-0 h-full w-full md:w-96 bg-white shadow-lg p-6
          transform transition-transform duration-500 ease-in-out
          z-50 overflow-auto
          "
          style={{ animation: "slideIn 0.5s forwards" }}
        >
          <button
            onClick={toggleModal}
            className="mb-4 text-red-600 font-bold"
          >
            Close ✕
          </button>

          {!editProfile ? (
            <>
              {/* Profile photo upload */}
              <label
                htmlFor="profilePhotoInput"
                className="block mx-auto w-28 h-28 rounded-full overflow-hidden cursor-pointer mb-4 border-2 border-green-600"
                title="Click to change profile photo"
              >
                <img
                  src={profile.photoUrl}
                  alt="Seller"
                  className="w-full h-full object-cover"
                />
              </label>
              <input
                id="profilePhotoInput"
                type="file"
                accept="image/*"
                capture="environment"
                style={{ display: "none" }}
                onChange={handleProfilePhotoChange}
              />

              <p><strong>Full Name:</strong> {profile.fullName}</p>
              <p><strong>Address:</strong> {profile.address}</p>
              <p><strong>Phone:</strong> {profile.phone}</p>
              <p><strong>Tailoring Name:</strong> {profile.tailoringName}</p>
              <p><strong>Email:</strong> {profile.email}</p>

              <button
                onClick={() => setEditProfile(true)}
                className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
              >
                Edit Profile
              </button>
            </>
          ) : (
            <>
              <div className="space-y-3">
                <label>
                  Full Name
                  <input
                    type="text"
                    name="fullName"
                    value={profile.fullName}
                    onChange={handleProfileChange}
                    className="w-full border p-2 rounded"
                  />
                </label>
                <label>
                  Address
                  <input
                    type="text"
                    name="address"
                    value={profile.address}
                    onChange={handleProfileChange}
                    className="w-full border p-2 rounded"
                  />
                </label>
                <label>
                  Phone
                  <input
                    type="text"
                    name="phone"
                    value={profile.phone}
                    onChange={handleProfileChange}
                    className="w-full border p-2 rounded"
                  />
                </label>
                <label>
                  Tailoring Name
                  <input
                    type="text"
                    name="tailoringName"
                    value={profile.tailoringName}
                    onChange={handleProfileChange}
                    className="w-full border p-2 rounded"
                  />
                </label>
                <label>
                  Email
                  <input
                    type="email"
                    name="email"
                    value={profile.email}
                    onChange={handleProfileChange}
                    className="w-full border p-2 rounded"
                  />
                </label>
              </div>
              <button
                onClick={saveProfile}
                className="mt-4 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
              >
                Save
              </button>
            </>
          )}
        </div>
        
      )}

      {/* Add slide-in keyframe animation */}
      <style>
        {`
          @keyframes slideIn {
            from {
              transform: translateX(100%);
              opacity: 0;
            }
            to {
              transform: translateX(0);
              opacity: 1;
            }
          }
        `}
      </style>
      
    </div>
  );
}

export default SellerDashboard;
