import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import DesignCard from "../components/DesignCard";

export default function CustomerDashboard({ sellers }) {
  const { sellerId } = useParams();
  const navigate = useNavigate();

  // Defensive: sellers might not be loaded yet
  if (!Array.isArray(sellers) || sellers.length === 0) {
    return <div className="p-10 text-gray-500 text-center">No sellers found.</div>;
  }

  // Use String() in case IDs are numbers or strings
  const seller = sellers.find(s => String(s.id) === String(sellerId));

  if (!seller) {
    return <div className="p-10 text-red-700 font-bold">Seller Not Found</div>;
  }

  // Defensive: if portfolios is missing or not array, show a message
  if (!Array.isArray(seller.portfolios) || seller.portfolios.length === 0) {
    return (
      <div className="flex flex-col items-center p-6">
        {/* Seller info top */}
        <div className="bg-white/80 p-4 rounded-xl shadow-lg mb-8">
          <div className="flex items-center gap-6">
            <img src={seller.photo_url} alt="Seller" className="w-24 h-24 rounded-full border-4 border-green-400 shadow" />
            <div>
              <h1 className="text-2xl font-bold text-green-700">{seller.shop_name}</h1>
              <div className="text-lg text-blue-600">{seller.shop_address}</div>
              <div className="mt-1 text-gray-700">{seller.gst_number}</div>
              <div className="text-gray-500">{seller.email}</div>
            </div>
          </div>
        </div>
        <h2 className="text-xl font-semibold text-blue-700 mb-4">Explore Designs</h2>
        <div className="text-center text-gray-500 my-8 w-full">No designs posted yet.</div>
      </div>
    );
  }

  return (
    <div className="flex flex-col items-center p-6">
      {/* Seller info top */}
      <div className="bg-white/80 p-4 rounded-xl shadow-lg mb-8">
        <div className="flex items-center gap-6">
          <img src={seller.photo_url} alt="Seller" className="w-24 h-24 rounded-full border-4 border-green-400 shadow" />
          <div>
            <h1 className="text-2xl font-bold text-green-700">{seller.shop_name}</h1>
            <div className="text-lg text-blue-600">{seller.shop_address}</div>
            <div className="mt-1 text-gray-700">{seller.gst_number}</div>
            <div className="text-gray-500">{seller.email}</div>
          </div>
        </div>
      </div>
      <h2 className="text-xl font-semibold text-blue-700 mb-4">Explore Designs</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 w-full max-w-5xl">
        {Array.isArray(seller.portfolios) && seller.portfolios.length > 0 ? (
          seller.portfolios.map((design, idx) => (
            <DesignCard
              key={idx}
              design={{
                imageUrl: design.image_url,
                title: design.title,
                description: design.description,
                contact: design.contact,
              }}
              onChat={() => navigate("/chat")}
              animate
            />
          ))
        ) : (
          <div className="text-center text-gray-500 my-8 w-full">No designs posted yet.</div>
        )}
      </div>
    </div>
  );
}
