import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import ProfileModal from "../components/ProfileModal";

function SellerDashboard() {
  const navigate = useNavigate();

  const [designs, setDesigns] = useState([]);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    imageUrl: "",
    contact: ""
  });
  const [editingIndex, setEditingIndex] = useState(null);

  const [profile, setProfile] = useState({
    fullName: "John Doe",
    address: "123 Tailoring Street, City",
    phone: "+91 9876543210",
    tailoringName: "John's Tailors",
    email: "john@example.com",
    photoUrl: "https://via.placeholder.com/80"
  });

  const [showModal, setShowModal] = useState(false);
  const [editProfile, setEditProfile] = useState(false);

  const titleInputRef = useRef(null);

  // --- Design form handlers ---
  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingIndex === null) {
      setDesigns((prev) => [...prev, formData]);
    } else {
      setDesigns((prev) => {
        const newDesigns = [...prev];
        newDesigns[editingIndex] = formData;
        return newDesigns;
      });
    }
    setFormData({ title: "", description: "", imageUrl: "", contact: "" });
    setEditingIndex(null);
    window.scrollTo({ top: 0, behavior: "smooth" });
    setTimeout(() => {
      titleInputRef.current?.focus();
    }, 300);
  };

  const handleEditDesign = (index) => {
    setEditingIndex(index);
    setFormData(designs[index]);
    window.scrollTo({ top: 0, behavior: "smooth" });
    setTimeout(() => {
      titleInputRef.current?.focus();
    }, 300);
  };

  // --- UI ---
  return (
    <div className="flex flex-col max-w-6xl mx-auto p-6 gap-6 relative">
      {/* Top action bar */}
      <div className="flex justify-end gap-3 mb-4">
        <button
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition shadow"
          onClick={() => navigate("/chat")}
        >
          Go to Chat
        </button>
        <button
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition shadow"
          onClick={() => setShowModal(true)}
        >
          Show Profile
        </button>
      </div>

      {/* Main content: Design form and list */}
      <div className="flex gap-6">
        {/* Left: Designs Form and List */}
        <div className="flex-1">
          <h2 className="text-2xl font-bold mb-4">
            {editingIndex === null ? "Post a New Design" : "Edit Design"}
          </h2>
          <form
            onSubmit={handleSubmit}
            className="space-y-4 bg-white p-4 shadow-md rounded mb-6"
          >
            <input
              ref={titleInputRef}
              className="w-full border p-2"
              type="text"
              name="title"
              placeholder="Design Title"
              value={formData.title}
              onChange={handleChange}
              required
            />
            <textarea
              className="w-full border p-2"
              name="description"
              placeholder="Description"
              value={formData.description}
              onChange={handleChange}
              required
            />
            <input
              className="w-full border p-2"
              type="text"
              name="imageUrl"
              placeholder="Image URL"
              value={formData.imageUrl}
              onChange={handleChange}
              required
            />
            <input
              className="w-full border p-2"
              type="text"
              name="contact"
              placeholder="Contact Info"
              value={formData.contact}
              onChange={handleChange}
              required
            />
            <button
              type="submit"
              className={`px-4 py-2 rounded ${
                editingIndex === null
                  ? "bg-green-600 hover:bg-green-700 text-white"
                  : "bg-yellow-600 hover:bg-yellow-700 text-white"
              }`}
            >
              {editingIndex === null ? "Post Design" : "Save Changes"}
            </button>
            {editingIndex !== null && (
              <button
                type="button"
                onClick={() => {
                  setEditingIndex(null);
                  setFormData({ title: "", description: "", imageUrl: "", contact: "" });
                }}
                className="ml-4 px-4 py-2 rounded bg-gray-400 hover:bg-gray-500 text-white"
              >
                Cancel
              </button>
            )}
          </form>

          <h3 className="text-xl font-semibold mb-2">Your Designs</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {designs.map((design, index) => (
              <div
                key={index}
                className="border p-4 rounded shadow bg-white cursor-pointer"
                onClick={() => handleEditDesign(index)}
                title="Click to edit"
              >
                <img
                  src={design.imageUrl}
                  alt="Design"
                  className="w-full h-48 object-cover rounded"
                />
                <h4 className="text-lg font-bold mt-2">{design.title}</h4>
                <p>{design.description}</p>
                <p className="text-sm text-gray-500 mt-1">Contact: {design.contact}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Right: Seller Profile Summary (optional) */}
        <div className="w-32 flex flex-col items-center">
          <img
            src={profile.photoUrl}
            alt="Seller"
            className="rounded-full w-24 h-24 object-cover border-2 border-green-600"
          />
          <p className="mt-2 font-semibold">{profile.fullName}</p>
        </div>
      </div>

      {/* Profile Modal */}
      <ProfileModal
        profile={profile}
        setProfile={setProfile}
        open={showModal}
        onClose={() => { setShowModal(false); setEditProfile(false); }}
        editable={editProfile}
        setEditable={setEditProfile}
      />
    </div>
  );
}

export default SellerDashboard;
