import React from "react";
import { useNavigate } from "react-router-dom";
import DesignCard from "../components/DesignCard";

export default function CustomerDashboard({ profile, designs }) {
  const navigate = useNavigate();
  return (
    <div className="flex flex-col items-center p-6">
      <div className="bg-white/80 p-4 rounded-xl shadow-lg mb-8 animate__animated animate__fadeInDown">
        <div className="flex items-center gap-6">
          <img src={profile.photoUrl} alt="Seller" className="w-24 h-24 rounded-full border-4 border-green-400 shadow" />
          <div>
            <h1 className="text-2xl font-bold text-green-700">{profile.fullName}</h1>
            <div className="text-lg text-blue-600">{profile.tailoringName}</div>
            <div className="mt-1 text-gray-700">{profile.address}</div>
            <div className="text-gray-500">{profile.phone} | {profile.email}</div>
          </div>
        </div>
      </div>
      <h2 className="text-xl font-semibold text-blue-700 mb-4">Explore Designs</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 w-full max-w-5xl">
        {designs.map((design, idx) => (
          <DesignCard
            key={idx}
            design={design}
            onChat={() => navigate("/chat")}
            animate
          />
        ))}
      </div>
    </div>
  );
}
