import React, { useState, useEffect, useRef } from "react";
import ChatSidebar from "../components/ChatSidebar";
import ProfileModal from "../components/ProfileModal";

// Dummy users for demo; replace with real data/auth in production.
const usersData = {
  "seller1@example.com": {
    fullName: "Anil Seller",
    tailoringName: "Anil's Tailoring",
    photoUrl: "https://randomuser.me/api/portraits/men/32.jpg",
    address: "22 Seller Street",
    phone: "+91 9845098450",
    email: "seller1@example.com",
  },
  "customer1@example.com": {
    fullName: "Sneha Customer",
    tailoringName: "",
    photoUrl: "https://randomuser.me/api/portraits/women/65.jpg",
    address: "11 Customer Lane",
    phone: "+91 9333093330",
    email: "customer1@example.com",
  },
};

function getDefaultProfile(email) {
  return usersData[email] || {
    fullName: "My Name",
    tailoringName: "",
    photoUrl: "https://via.placeholder.com/80",
    address: "",
    phone: "",
    email,
  };
}

function getChatsForUser(userEmail) {
  const data = localStorage.getItem(`tailoring_chats_${userEmail}`);
  return data ? JSON.parse(data) : [];
}

function setChatsForUser(userEmail, chats) {
  localStorage.setItem(`tailoring_chats_${userEmail}`, JSON.stringify(chats));
}

// Main function: adds a message for both sender and receiver.
function addMessage(senderEmail, receiverEmail, msgText) {
  // Sender's chat list
  let senderChats = getChatsForUser(senderEmail);
  let senderIdx = senderChats.findIndex(chat => chat.with === receiverEmail);
  if (senderIdx === -1) {
    senderChats.push({ with: receiverEmail, messages: [], lastMsg: "", unread: 0 });
    senderIdx = senderChats.length - 1;
  }
  senderChats[senderIdx].messages.push({ sender: senderEmail, text: msgText, time: Date.now(), read: true });
  senderChats[senderIdx].lastMsg = msgText;
  senderChats[senderIdx].unread = 0;
  setChatsForUser(senderEmail, senderChats);

  // Receiver's chat list
  let receiverChats = getChatsForUser(receiverEmail);
  let receiverIdx = receiverChats.findIndex(chat => chat.with === senderEmail);
  if (receiverIdx === -1) {
    receiverChats.push({ with: senderEmail, messages: [], lastMsg: "", unread: 0 });
    receiverIdx = receiverChats.length - 1;
  }
  receiverChats[receiverIdx].messages.push({ sender: senderEmail, text: msgText, time: Date.now(), read: false });
  receiverChats[receiverIdx].lastMsg = msgText;
  receiverChats[receiverIdx].unread = (receiverChats[receiverIdx].unread || 0) + 1;
  setChatsForUser(receiverEmail, receiverChats);
}

function markChatRead(userEmail, withEmail) {
  let chats = getChatsForUser(userEmail);
  let idx = chats.findIndex(chat => chat.with === withEmail);
  if (idx !== -1) {
    chats[idx].unread = 0;
    chats[idx].messages = chats[idx].messages.map(msg => ({ ...msg, read: true }));
    setChatsForUser(userEmail, chats);
  }
}

export default function ChatMutual() {
  // Set this for demo. In real apps, get from auth (localStorage, context, etc)
  const [userEmail] = useState("seller1@example.com");
  const [profile, setProfile] = useState(getDefaultProfile("seller1@example.com"));
  const [showProfile, setShowProfile] = useState(false);
  const [editable, setEditable] = useState(false);

  // Chat state
  const [chats, setChats] = useState([]);
  const [selected, setSelected] = useState(null); // withEmail
  const [message, setMessage] = useState("");
  const [userProfiles] = useState(usersData);
  const [unreadMap, setUnreadMap] = useState({});

  const chatBodyRef = useRef(null);

  // Load chats on mount
  useEffect(() => {
    setProfile(getDefaultProfile(userEmail));
    setChats(getChatsForUser(userEmail));
    setSelected(null);
    setUnreadMap(Object.fromEntries(getChatsForUser(userEmail).map(c => [c.with, c.unread || 0])));
  }, [userEmail]);

  // On open chat, mark as read
  useEffect(() => {
    if (selected) {
      markChatRead(userEmail, selected);
      setChats(getChatsForUser(userEmail));
      setUnreadMap(Object.fromEntries(getChatsForUser(userEmail).map(c => [c.with, c.unread || 0])));
    }
    if (chatBodyRef.current) {
      setTimeout(() => { chatBodyRef.current.scrollTop = chatBodyRef.current.scrollHeight; }, 100);
    }
  }, [selected, userEmail]);

  // Send message
  function handleSend() {
    if (!message.trim()) return;
    addMessage(userEmail, selected, message);
    setChats(getChatsForUser(userEmail));
    setMessage("");
    setTimeout(() => {
      // Simulate reply from other user after 1s
      const reply = userEmail.includes("seller")
        ? "Thank you! I'll reply soon."
        : "Thank you for reaching out!";
      addMessage(selected, userEmail, reply);
      setChats(getChatsForUser(userEmail));
      setUnreadMap(Object.fromEntries(getChatsForUser(userEmail).map(c => [c.with, c.unread || 0])));
    }, 1000);
  }

  return (
    <div className="flex h-screen bg-gradient-to-tr from-blue-100 to-green-100">
      {/* Sidebar */}
      <ChatSidebar
        chats={chats}
        selectedId={selected}
        onSelect={setSelected}
        userProfiles={userProfiles}
        unreadMap={unreadMap}
      />
      {/* Main Chat */}
      <div className="flex-1 flex flex-col">
        {/* Profile bar at the top */}
        <div className="flex items-center justify-between p-4 bg-white shadow">
          <div className="flex items-center gap-3">
            <img src={profile.photoUrl} alt="me" className="w-12 h-12 rounded-full border-2 border-green-600 object-cover cursor-pointer" onClick={() => setShowProfile(true)} />
            <div>
              <div className="font-bold">{profile.fullName}</div>
              <div className="text-xs text-gray-400">{profile.tailoringName || profile.email}</div>
            </div>
          </div>
        </div>
        <div className="flex-1 flex flex-col">
          {selected ? (
            <>
              {/* Chat Header */}
              <div className="flex items-center gap-3 p-3 border-b bg-gradient-to-r from-blue-100 to-green-100">
                <img src={userProfiles[selected]?.photoUrl || "https://via.placeholder.com/40"} className="w-10 h-10 rounded-full border" alt="" />
                <div>
                  <div className="font-semibold">{userProfiles[selected]?.fullName || selected}</div>
                  <div className="text-xs text-gray-500">{userProfiles[selected]?.tailoringName}</div>
                </div>
              </div>
              {/* Chat Body */}
              <div ref={chatBodyRef} className="flex-1 overflow-y-auto p-6 space-y-2 bg-gradient-to-br from-white to-blue-50 transition-all">
                {(chats.find(c => c.with === selected)?.messages || []).map((msg, idx) => (
                  <div key={idx} className={`flex ${msg.sender === userEmail ? "justify-end" : "justify-start"}`}>
                    <div className={`max-w-xs px-4 py-2 rounded-2xl shadow ${msg.sender === userEmail ? "bg-green-500 text-white animate-bounce-in-right" : "bg-white border"}`}>
                      {msg.text}
                      <div className="text-xs text-right mt-1 text-gray-300">{new Date(msg.time).toLocaleTimeString()}</div>
                    </div>
                  </div>
                ))}
              </div>
              {/* Chat Input */}
              <div className="p-3 border-t flex gap-2 bg-white">
                <input
                  className="flex-1 border rounded px-3 py-2 focus:ring-2 focus:ring-green-300"
                  placeholder="Type your message..."
                  value={message}
                  onChange={e => setMessage(e.target.value)}
                  onKeyDown={e => e.key === "Enter" && handleSend()}
                />
                <button className="bg-green-600 text-white px-5 py-2 rounded hover:bg-green-700 transition" onClick={handleSend}>Send</button>
              </div>
            </>
          ) : (
            <div className="flex-1 flex flex-col items-center justify-center text-gray-400">
              <img src="/chat-illustration.svg" alt="" className="w-40 opacity-60 mb-4" />
              <div className="font-bold text-2xl">Select a chat to start messaging</div>
            </div>
          )}
        </div>
      </div>
      {/* Profile Modal */}
      <ProfileModal
        profile={profile}
        setProfile={setProfile}
        open={showProfile}
        onClose={() => { setShowProfile(false); setEditable(false); }}
        editable={editable}
        setEditable={setEditable}
      />
      {/* Animation keyframes */}
      <style>
        {`
          @keyframes bounce-in-right {
            0% { opacity: 0; transform: translateX(80px) scale(0.8);}
            80% { opacity: 1; transform: translateX(-10px) scale(1.05);}
            100% { opacity: 1; transform: translateX(0) scale(1);}
          }
          .animate-bounce-in-right {
            animation: bounce-in-right 0.4s;
          }
        `}
      </style>
    </div>
  );
}
