import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import Home from "./pages/Home";
import SellerDashboard from "./pages/SellerDashboard";
import CustomerDashboard from "./pages/CustomerDashboard";
import ChatPage from "./pages/ChatPage";
import Login from "./pages/Login";
import Register from "./pages/Register";
import SellerListPage from "./pages/SellerListPage";

// Sample/mock seller data
const MOCK_SELLERS = [
  {
    id: 1,
    fullName: "Ravi Kumar",
    tailoringName: "Ravi Tailors",
    phone: "9876543210",
    address: "123 MG Road",
    email: "ravi@tailors.com",
    photoUrl: "https://randomuser.me/api/portraits/men/1.jpg",
    gstNumber: "GSTIN12345",
    designs: [
      {
        imageUrl: "https://source.unsplash.com/400x400/?dress,1",
        title: "Red Bridal Dress",
        description: "Elegant bridal wear with embroidery"
      },
      {
        imageUrl: "https://source.unsplash.com/400x400/?dress,2",
        title: "Blue Party Gown",
        description: "Stylish gown for parties"
      }
    ]
  },
  {
    id: 2,
    fullName: "Sita Devi",
    tailoringName: "Sita Boutique",
    phone: "9123456780",
    address: "22 Main Street",
    email: "sita@boutique.com",
    photoUrl: "https://randomuser.me/api/portraits/women/2.jpg",
    gstNumber: "GSTIN67890",
    designs: [
      {
        imageUrl: "https://source.unsplash.com/400x400/?saree,1",
        title: "Kanchipuram Saree",
        description: "Traditional silk saree"
      }
    ]
  }
];

function App() {
  const [profile, setProfile] = useState(null);
  const [sellers, setSellers] = useState([]);

  // Simulate fetching sellers from an API
  useEffect(() => {
    // Replace this with your real API call
    setTimeout(() => setSellers(MOCK_SELLERS), 500);
  }, []);

  return (
    <Router>
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-green-50 to-blue-100 animate-bgMove">
        <Routes>
          <Route path="/" element={<Login setProfile={setProfile} />} />
          <Route path="/login" element={<Login setProfile={setProfile} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/home" element={<Home profile={profile} />} />
          <Route path="/seller" element={
            <SellerDashboard
              profile={profile}
              setProfile={setProfile}
            />
          } />
          {/* Pass sellers as prop */}
          <Route path="/customer" element={<SellerListPage sellers={sellers} />} />
          <Route path="/customer/:sellerId" element={<CustomerDashboard sellers={sellers} />} />
          <Route path="/chat" element={
            <ChatPage profile={profile} />
          } />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
      {/* ...animated background style... */}
    </Router>
  );
}

export default App;
